import { Component, OnInit } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../../core/services/auth.service';
import { HabitService } from '../../core/services/habit.service';
import { LeaderboardService } from '../../core/services/leaderboard.service';
import { User } from '../../core/models/user.model';
import { Habit } from '../../core/models/habit.model';
import { UserRank } from '../../core/services/leaderboard.service';

@Component({
  selector: 'app-dashboard',
  template: `
    <div class="min-h-screen bg-gray-50">
      <!-- Header -->
      <header class="bg-white shadow-sm border-b">
        <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div class="flex justify-between items-center h-16">
            <div class="flex items-center space-x-4">
              <h1 class="text-2xl font-bold text-gray-900">üéØ HabitTracker</h1>
            </div>
            <div class="flex items-center space-x-4">
              <div class="text-right">
                <p class="text-sm text-gray-600">Welcome back,</p>
                <p class="font-semibold text-gray-900">{{ user?.username }}</p>
              </div>
              <button 
                (click)="logout()"
                class="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-lg transition-colors"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </header>

      <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <!-- Stats Cards -->
        <div class="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <div class="bg-gradient-to-r from-purple-500 to-purple-600 rounded-xl p-6 text-white">
            <div class="flex items-center justify-between">
              <div>
                <p class="text-purple-100">Level</p>
                <p class="text-3xl font-bold">{{ user?.level }}</p>
              </div>
              <div class="text-4xl">üèÜ</div>
            </div>
          </div>

          <div class="bg-gradient-to-r from-blue-500 to-blue-600 rounded-xl p-6 text-white">
            <div class="flex items-center justify-between">
              <div>
                <p class="text-blue-100">Total XP</p>
                <p class="text-3xl font-bold">{{ user?.totalXP | number }}</p>
              </div>
              <div class="text-4xl">‚≠ê</div>
            </div>
          </div>

          <div class="bg-gradient-to-r from-green-500 to-green-600 rounded-xl p-6 text-white">
            <div class="flex items-center justify-between">
              <div>
                <p class="text-green-100">Rank</p>
                <p class="text-3xl font-bold">#{{ userRank?.rank }}</p>
              </div>
              <div class="text-4xl">ü•á</div>
            </div>
          </div>

          <div class="bg-gradient-to-r from-orange-500 to-orange-600 rounded-xl p-6 text-white">
            <div class="flex items-center justify-between">
              <div>
                <p class="text-orange-100">Completed</p>
                <p class="text-3xl font-bold">{{ user?.stats?.totalHabitsCompleted }}</p>
              </div>
              <div class="text-4xl">‚úÖ</div>
            </div>
          </div>
        </div>

        <!-- Main Content -->
        <div class="grid grid-cols-1 lg:grid-cols-3 gap-8">
          <!-- Habits Section -->
          <div class="lg:col-span-2">
            <div class="bg-white rounded-xl shadow-sm p-6">
              <div class="flex justify-between items-center mb-6">
                <h2 class="text-xl font-bold text-gray-900">Today's Habits</h2>
                <button 
                  (click)="showCreateHabit = true"
                  class="bg-purple-500 hover:bg-purple-600 text-white px-4 py-2 rounded-lg transition-colors"
                >
                  + Add Habit
                </button>
              </div>

              <div *ngIf="habits.length === 0" class="text-center py-12">
                <div class="text-6xl mb-4">üéØ</div>
                <h3 class="text-lg font-semibold text-gray-900 mb-2">No habits yet</h3>
                <p class="text-gray-600">Create your first habit to start your journey!</p>
              </div>

              <div class="space-y-4">
                <div *ngFor="let habit of habits" 
                     class="border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow">
                  <div class="flex items-center justify-between">
                    <div class="flex-1">
                      <div class="flex items-center space-x-3">
                        <h3 class="font-semibold text-gray-900">{{ habit.name }}</h3>
                        <span class="px-2 py-1 text-xs rounded-full"
                              [ngClass]="{
                                'bg-green-100 text-green-800': habit.difficulty === 'easy',
                                'bg-yellow-100 text-yellow-800': habit.difficulty === 'medium',
                                'bg-red-100 text-red-800': habit.difficulty === 'hard'
                              }">
                          {{ habit.difficulty }}
                        </span>
                      </div>
                      <p class="text-gray-600 text-sm mt-1">{{ habit.description }}</p>
                      <div class="flex items-center space-x-4 mt-2">
                        <span class="text-sm text-gray-500">üî• {{ habit.currentStreak }} day streak</span>
                        <span class="text-sm text-gray-500">üìà Best: {{ habit.longestStreak }}</span>
                      </div>
                    </div>
                    <div class="flex items-center space-x-2">
                      <button 
                        (click)="completeHabit(habit._id)"
                        [disabled]="isCompletedToday(habit)"
                        class="px-4 py-2 rounded-lg font-medium transition-colors"
                        [ngClass]="{
                          'bg-green-500 hover:bg-green-600 text-white': !isCompletedToday(habit),
                          'bg-gray-300 text-gray-500 cursor-not-allowed': isCompletedToday(habit)
                        }"
                      >
                        {{ isCompletedToday(habit) ? '‚úì Done' : 'Complete' }}
                      </button>
                      <button 
                        (click)="deleteHabit(habit._id)"
                        class="text-red-500 hover:text-red-700 p-2"
                      >
                        üóëÔ∏è
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>

          <!-- Sidebar -->
          <div class="space-y-6">
            <!-- Achievements -->
            <div class="bg-white rounded-xl shadow-sm p-6">
              <h3 class="text-lg font-bold text-gray-900 mb-4">üèÜ Recent Achievements</h3>
              <div *ngIf="user?.achievements?.length === 0" class="text-center py-4">
                <p class="text-gray-500">No achievements yet</p>
              </div>
              <div class="space-y-3">
                <div *ngFor="let achievement of user?.achievements?.slice(0, 3)" 
                     class="flex items-center space-x-3 p-3 bg-yellow-50 rounded-lg">
                  <div class="text-2xl">üèÜ</div>
                  <div>
                    <p class="font-semibold text-gray-900">{{ achievement.name }}</p>
                    <p class="text-sm text-gray-600">{{ achievement.unlockedAt | date:'short' }}</p>
                  </div>
                </div>
              </div>
            </div>

            <!-- Progress -->
            <div class="bg-white rounded-xl shadow-sm p-6">
              <h3 class="text-lg font-bold text-gray-900 mb-4">üìä Progress</h3>
              <div class="space-y-4">
                <div>
                  <div class="flex justify-between text-sm mb-1">
                    <span>Level Progress</span>
                    <span>{{ getLevelProgress() }}%</span>
                  </div>
                  <div class="w-full bg-gray-200 rounded-full h-2">
                    <div class="bg-purple-500 h-2 rounded-full transition-all" 
                         [style.width.%]="getLevelProgress()"></div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <!-- Create Habit Modal -->
      <div *ngIf="showCreateHabit" class="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
        <div class="bg-white rounded-xl p-6 w-full max-w-md">
          <h3 class="text-lg font-bold mb-4">Create New Habit</h3>
          <form [formGroup]="habitForm" (ngSubmit)="createHabit()">
            <div class="space-y-4">
              <div>
                <label class="block text-sm font-medium mb-1">Name</label>
                <input formControlName="name" class="w-full px-3 py-2 border rounded-lg">
              </div>
              <div>
                <label class="block text-sm font-medium mb-1">Description</label>
                <textarea formControlName="description" class="w-full px-3 py-2 border rounded-lg"></textarea>
              </div>
              <div>
                <label class="block text-sm font-medium mb-1">Category</label>
                <select formControlName="category" class="w-full px-3 py-2 border rounded-lg">
                  <option value="health">Health</option>
                  <option value="fitness">Fitness</option>
                  <option value="learning">Learning</option>
                  <option value="productivity">Productivity</option>
                  <option value="mindfulness">Mindfulness</option>
                </select>
              </div>
              <div>
                <label class="block text-sm font-medium mb-1">Difficulty</label>
                <select formControlName="difficulty" class="w-full px-3 py-2 border rounded-lg">
                  <option value="easy">Easy (8 XP)</option>
                  <option value="medium">Medium (10 XP)</option>
                  <option value="hard">Hard (15 XP)</option>
                </select>
              </div>
            </div>
            <div class="flex space-x-3 mt-6">
              <button type="submit" class="flex-1 bg-purple-500 text-white py-2 rounded-lg">Create</button>
              <button type="button" (click)="showCreateHabit = false" class="flex-1 bg-gray-300 py-2 rounded-lg">Cancel</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  `
})
export class DashboardComponent implements OnInit {
  user: User | null = null;
  habits: Habit[] = [];
  userRank: UserRank | null = null;
  showCreateHabit = false;
  habitForm = this.fb.group({
    name: ['', Validators.required],
    description: [''],
    category: ['health', Validators.required],
    difficulty: ['medium', Validators.required],
    targetFrequency: [1]
  });

  constructor(
    private authService: AuthService,
    private habitService: HabitService,
    private leaderboardService: LeaderboardService,
    private fb: FormBuilder,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.user = this.authService.getCurrentUser();
    this.loadHabits();
    this.loadUserRank();
  }

  loadHabits(): void {
    this.habitService.getHabits().subscribe(habits => {
      this.habits = habits;
    });
  }

  loadUserRank(): void {
    this.leaderboardService.getUserRank().subscribe(rank => {
      this.userRank = rank;
    });
  }

  createHabit(): void {
    if (this.habitForm.valid) {
      this.habitService.createHabit(this.habitForm.value as any).subscribe(() => {
        this.loadHabits();
        this.showCreateHabit = false;
        this.habitForm.reset({ category: 'health', difficulty: 'medium', targetFrequency: 1 });
      });
    }
  }

  completeHabit(habitId: string): void {
    this.habitService.completeHabit(habitId).subscribe(response => {
      this.loadHabits();
      // Update user XP and level
      if (this.user) {
        this.user.totalXP += response.xpEarned;
        this.user.level = response.newLevel;
      }
    });
  }

  deleteHabit(habitId: string): void {
    if (confirm('Are you sure you want to delete this habit?')) {
      this.habitService.deleteHabit(habitId).subscribe(() => {
        this.loadHabits();
      });
    }
  }

  isCompletedToday(habit: Habit): boolean {
    if (!habit.lastCompletedDate) return false;
    const today = new Date().toDateString();
    return new Date(habit.lastCompletedDate).toDateString() === today;
  }

  getLevelProgress(): number {
    if (!this.user) return 0;
    const currentLevelXP = (this.user.level - 1) * (this.user.level - 1) * 50;
    const nextLevelXP = this.user.level * this.user.level * 50;
    const progress = ((this.user.totalXP - currentLevelXP) / (nextLevelXP - currentLevelXP)) * 100;
    return Math.min(100, Math.max(0, progress));
  }

  logout(): void {
    this.authService.logout();
    this.router.navigate(['/login']);
  }
}
