import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { AuthService } from '../../core/services/auth.service';

@Component({
  selector: 'app-admin-panel',
  template: `
    <div *ngIf="isAdmin" class="min-h-screen bg-gray-50 dark:bg-gray-900">
      <div class="max-w-7xl mx-auto px-4 py-8">
        <h1 class="text-3xl font-bold text-gray-900 dark:text-white mb-8">üîê Admin Panel</h1>

        <!-- Stats -->
        <div class="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          <div class="bg-white dark:bg-gray-800 rounded-xl p-6">
            <h2 class="text-xl font-bold text-gray-900 dark:text-white mb-4">System Stats</h2>
            <div class="space-y-2">
              <p class="text-gray-600 dark:text-gray-300">Total Users: {{ stats?.userCount || 0 }}</p>
              <p class="text-gray-600 dark:text-gray-300">Total Habits: {{ stats?.habitCount || 0 }}</p>
            </div>
          </div>
        </div>

        <!-- Users Table -->
        <div class="bg-white dark:bg-gray-800 rounded-xl shadow-sm p-6 mb-6">
          <div class="flex justify-between items-center mb-4">
            <h2 class="text-xl font-bold text-gray-900 dark:text-white">Users</h2>
            <div class="flex space-x-4">
              <input
                [(ngModel)]="searchTerm"
                (input)="loadUsers()"
                placeholder="Search users..."
                class="px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
              >
              <button
                (click)="clearAllData()"
                class="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-lg"
              >
                Clear All Data
              </button>
            </div>
          </div>

          <div *ngIf="loading" class="text-center py-8">
            <app-loader text="Loading users..."></app-loader>
          </div>

          <div *ngIf="!loading" class="overflow-x-auto">
            <table class="min-w-full divide-y divide-gray-200 dark:divide-gray-700">
              <thead class="bg-gray-50 dark:bg-gray-700">
                <tr>
                  <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase">User</th>
                  <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase">Level</th>
                  <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase">XP</th>
                  <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase">Actions</th>
                </tr>
              </thead>
              <tbody class="bg-white dark:bg-gray-800 divide-y divide-gray-200 dark:divide-gray-700">
                <tr *ngFor="let user of users">
                  <td class="px-6 py-4 whitespace-nowrap">
                    <div class="text-sm font-medium text-gray-900 dark:text-white">{{ user.username }}</div>
                    <div class="text-sm text-gray-500 dark:text-gray-400">{{ user.email }}</div>
                  </td>
                  <td class="px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white">{{ user.level }}</td>
                  <td class="px-6 py-4 whitespace-nowrap">
                    <input
                      [(ngModel)]="user.totalXP"
                      (blur)="updateUserXP(user._id, user.totalXP)"
                      type="number"
                      class="w-20 px-2 py-1 border border-gray-300 dark:border-gray-600 rounded text-sm bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
                    >
                  </td>
                  <td class="px-6 py-4 whitespace-nowrap text-sm">
                    <button
                      (click)="deleteUser(user._id)"
                      class="text-red-600 hover:text-red-900 dark:text-red-400"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              </tbody>
            </table>

            <!-- Pagination -->
            <div class="flex justify-between items-center mt-4">
              <span class="text-sm text-gray-700 dark:text-gray-300">
                Page {{ currentPage }} of {{ totalPages }}
              </span>
              <div class="flex space-x-2">
                <button
                  (click)="changePage(currentPage - 1)"
                  [disabled]="currentPage === 1"
                  class="px-3 py-2 border rounded-lg disabled:opacity-50 bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
                >
                  Previous
                </button>
                <button
                  (click)="changePage(currentPage + 1)"
                  [disabled]="currentPage === totalPages"
                  class="px-3 py-2 border rounded-lg disabled:opacity-50 bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
                >
                  Next
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>

    <div *ngIf="!isAdmin" class="min-h-screen flex items-center justify-center">
      <div class="text-center">
        <h1 class="text-2xl font-bold text-gray-900 dark:text-white mb-4">Access Denied</h1>
        <p class="text-gray-600 dark:text-gray-300">Admin access required</p>
      </div>
    </div>
  `
})
export class AdminPanelComponent implements OnInit {
  isAdmin = false;
  users: any[] = [];
  stats: any = null;
  loading = false;
  searchTerm = '';
  currentPage = 1;
  totalPages = 1;

  constructor(
    private http: HttpClient,
    private authService: AuthService
  ) {}

  ngOnInit(): void {
    const user = this.authService.getCurrentUser();
    this.isAdmin = user?.isAdmin || false;

    if (this.isAdmin) {
      this.loadStats();
      this.loadUsers();
    }
  }

  loadStats(): void {
    this.http.get('http://localhost:3000/api/admin/stats').subscribe(stats => {
      this.stats = stats;
    });
  }

  loadUsers(): void {
    this.loading = true;
    this.http.get(`http://localhost:3000/api/admin/users?page=${this.currentPage}&search=${this.searchTerm}`)
      .subscribe((response: any) => {
        this.users = response.users;
        this.totalPages = response.pagination.pages;
        this.loading = false;
      });
  }

  changePage(page: number): void {
    if (page >= 1 && page <= this.totalPages) {
      this.currentPage = page;
      this.loadUsers();
    }
  }

  updateUserXP(userId: string, totalXP: number): void {
    this.http.put(`http://localhost:3000/api/admin/users/${userId}/xp`, { totalXP }).subscribe();
  }

  deleteUser(userId: string): void {
    if (confirm('Delete this user?')) {
      this.http.delete(`http://localhost:3000/api/admin/users/${userId}`).subscribe(() => {
        this.loadUsers();
        this.loadStats();
      });
    }
  }

  clearAllData(): void {
    if (confirm('‚ö†Ô∏è This will delete ALL user data. Continue?')) {
      this.http.delete('http://localhost:3000/api/admin/clear-all').subscribe(() => {
        this.loadUsers();
        this.loadStats();
      });
    }
  }
}
