export class AdminPanelComponent {
  constructor(http, authService) {
    this.http = http;
    this.authService = authService;
    this.isAdmin = false;
    this.users = [];
    this.stats = null;
    this.loading = false;
    this.searchTerm = '';
    this.currentPage = 1;
    this.totalPages = 1;
    this.activeTab = 'users';
    this.allHabits = [];
    this.systemLogs = [];
    this.showHabitsModal = false;
    this.showChallengesModal = false;
    this.showAchievementsModal = false;
    this.showSystemModal = false;
    this.tabs = [{
      id: 'users',
      label: 'Users'
    }, {
      id: 'habits',
      label: 'Habits'
    }, {
      id: 'challenges',
      label: 'Challenges'
    }, {
      id: 'achievements',
      label: 'Achievements'
    }, {
      id: 'system',
      label: 'System'
    }, {
      id: 'analytics',
      label: 'Analytics'
    }];
  }
  ngOnInit() {
    const user = this.authService.getCurrentUser();
    console.log('Current user:', user); // Debug log
    this.isAdmin = user?.isAdmin === true;
    if (this.isAdmin) {
      this.loadStats();
      this.loadUsers();
    } else {
      console.log('User is not admin:', user?.isAdmin); // Debug log
    }
  }

  loadStats() {
    this.http.get('http://localhost:3000/api/admin/stats').subscribe(stats => {
      this.stats = stats;
    });
  }
  loadUsers() {
    this.loading = true;
    this.http.get(`http://localhost:3000/api/admin/users?page=${this.currentPage}&search=${this.searchTerm}`).subscribe(response => {
      this.users = response.users;
      this.totalPages = response.pagination.pages;
      this.loading = false;
    });
  }
  changePage(page) {
    if (page >= 1 && page <= this.totalPages) {
      this.currentPage = page;
      this.loadUsers();
    }
  }
  updateUserXP(userId, totalXP) {
    this.http.put(`http://localhost:3000/api/admin/users/${userId}/xp`, {
      totalXP
    }).subscribe();
  }
  deleteUser(userId) {
    if (confirm('Delete this user?')) {
      this.http.delete(`http://localhost:3000/api/admin/users/${userId}`).subscribe(() => {
        this.loadUsers();
        this.loadStats();
      });
    }
  }
  clearAllData() {
    if (confirm('‚ö†Ô∏è This will delete ALL user data. Continue?')) {
      this.http.delete('http://localhost:3000/api/admin/clear-all').subscribe(() => {
        this.loadUsers();
        this.loadStats();
      });
    }
  }
  loadAllHabits() {
    this.http.get('http://localhost:3000/api/admin/habits').subscribe(response => {
      this.allHabits = response.habits || [];
    });
  }
  deleteHabitAdmin(habitId) {
    if (confirm('Delete this habit?')) {
      this.http.delete(`http://localhost:3000/api/admin/habits/${habitId}`).subscribe(() => {
        this.loadAllHabits();
      });
    }
  }
  deleteInactiveHabits() {
    if (confirm('Delete all inactive habits?')) {
      this.http.delete('http://localhost:3000/api/admin/habits/inactive').subscribe(() => {
        this.loadAllHabits();
      });
    }
  }
  exportHabitsData() {
    this.http.get('http://localhost:3000/api/admin/export/habits', {
      responseType: 'blob'
    }).subscribe(blob => {
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'habits-export.json';
      a.click();
    });
  }
  exportAllData() {
    this.http.get('http://localhost:3000/api/admin/export/all', {
      responseType: 'blob'
    }).subscribe(blob => {
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'system-export.json';
      a.click();
    });
  }
  resetAllUserXP() {
    if (confirm('Reset ALL users XP to 0?')) {
      this.http.post('http://localhost:3000/api/admin/reset-xp', {}).subscribe(() => {
        this.loadUsers();
        this.loadStats();
        alert('All user XP reset successfully!');
      });
    }
  }
  sendBulkNotification() {
    const message = prompt('Enter notification message:');
